import "../styles/search.scss";
import searchIco from "../images/search.svg";
import React, { useEffect, useState, useRef } from "react";
import { Navigate } from "react-router-dom";
export function Search(props) {
  const [searchState, setSearchState] = React.useState(false);
  const [query, setQuery] = useState("");
  // function useOutsideAlerter(ref) {
  //   useEffect(() => {
  //     function handleClickOutside(event) {
  //       if (ref.current && !ref.current.contains(event.target)) {
  //         closeSearch();
  //       }
  //     }
  //     document.addEventListener("mousedown", handleClickOutside);
  //     return () => {
  //       document.removeEventListener("mousedown", handleClickOutside);
  //     };
  //   }, [ref]);
  // }
  // const wrapperRef = useRef(null);
  // useOutsideAlerter(wrapperRef);
  React.useEffect(() => {
    if (props.show) {
      openSearch();
    } else {
      closeSearch();
    }
  });
  const handleSubmit = (e) => {
    e.preventDefault();
    e.stopPropagation();
    Navigate("search" + query);
    return false;
  };
  const closeSearch = (e) => {
    setSearchState(false);
    props.closeParent(false);
  };
  const openSearch = () => {
    setSearchState(true);
  };
  const handleClick = (event) => {
    event.stopPropagation();
  };
  // React.useEffect(() => {
  if (searchState === true) {
    return (
      <>
        <div className="search-box-backdrop">
          <div className="search">
            <form
              class="search-form"
              name="search"
              method="post"
              // action="https://cattila.becomingcelia.com/zb_system/cmd.php?act=search"
              // action="/search?q="
              // ref={wrapperRef}
              onSubmit={() => handleSubmit()}
            >
              <input
                class="search-field"
                id="searchfield"
                type="text"
                name="q"
                placeholder="Search games and subject topicsâ€¦"
                onChange={(event) => setQuery(event.target.value)}
              />
              <button
                class="appear-button always-appear"
                type="submit"
                id="searchsubmit"
              >
                <img className="search-icon" src={searchIco} />
                <i class="icon icon-search" aria-hidden="true"></i>
              </button>
              <button
                class="search-button"
                onClick={() => Navigate("search" + query)}
              >
                <i class="icon icon-search" aria-hidden="true"></i>
              </button>
            </form>
          </div>
          <button
            onClick={() => closeSearch()}
            class="search-wrapper-close"
            aria-label="Close"
          ></button>
        </div>
      </>
    );
  } else if (searchState === false) {
    return null;
  }
}
