import "../styles/aboutPage.scss";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
function AboutPage() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const navigate = useNavigate();
  return (
    <div className="page-container">
      <div className="main-about black">
        <h1>ABOUT MERAKEY</h1>
        <p>
          <b>What is Merakey?</b>
        </p>
        <p>
          A learning platform, a gaming website, a young communityâ€¦ It can be a
          combination of all of these, and, to be honest, its definition is
          totally depending on <b>you</b>.
        </p>
        <p>
          Merakey is a learning platform for middle school and high school
          students enrolling in curriculums of IGCSE and O'level. By providing a
          rich resource of practice questions and past exam questions together
          with intrigueing interactive text games of a variety of genres,
          Merakey provides the best way for you to focus on doing practicing
          questions without procrastination, to relax in the process.
        </p>
        <blockquote>
          <p>You define your learning.</p>
        </blockquote>
        <p>
          Merakey offers great flexibility. different types of games, it is up
          to your preference. Step 1: choose the area you would like to revise
          for from our database. Step 2: inject the questions from the database
          into any of the text games on the site.
        </p>
      </div>
    </div>
  );
}
export default AboutPage;
