import "../styles/navBar.scss";
import logo from "../images/logo.png";
import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { Search } from "./search";

function NavBar() {
  React.useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const path = window.location.pathname.split("/")[1];
  const navigate = useNavigate();
  const openSearch = () => {
    // setSearchState(true);
    window.localStorage.setItem("search", "opened");
  };
  console.log("search state: " + searchState);
  // function ShowSearch(props) {
  //   const search = props.show;
  //   if (search) {
  //     return <Search show={true} />;
  //   } else {
  //     return <Search show={false} />;
  //   }
  // }

  return (
    <>
      {/* <ShowSearch show={searchState} /> */}
      {/* <ShowSearch /> */}
      <Search />

      <div className="navBar">
        <div className="container">
          <div className="logo-container" onClick={() => navigate("/")}>
            <img className="logo" src={logo} />
          </div>
          <div className="navigation">
            <ul>
              <li className={path === "" ? "active" : ""}>
                <a onClick={() => navigate("/")}>Home</a>
              </li>
              <li className={path === "about" ? "active" : ""}>
                <a onClick={() => navigate("/about")}>About</a>
              </li>
              <li className={path === "subjects" ? "active" : ""}>
                <a onClick={() => navigate("/subjects")}>Subjects</a>
              </li>
              <li>
                <a href="#">Upgrade</a>
              </li>
              <li>
                <a onClick={() => openSearch()}>Search</a>
              </li>
              <li>
                <a className="li-btn" href="#">
                  Sign up
                </a>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </>
  );
}
export default NavBar;
