import "../styles/searchPage.scss";
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import searchIco from "../images/search.svg";
import arrowUpIco from "../images/chevron-up.svg";
import arrowDownIco from "../images/chevron-down.svg";
import userIco from "../images/user.svg";
import clockIco from "../images/clock.svg";
import playIco from "../images/play-circle.svg";

function SearchPage() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const { query } = useParams();
  console.log("query: " + query);
  const [queryMain, setQueryMain] = useState(query);
  const mountedRef = React.useRef(true); // ← the "flag"
  // 在传进来的 parameter（即链接变量）改变的时候更新 state
  React.useEffect(() => {
    setQueryMain(query);
  }, [query]);
  const [mainTab, setMainTab] = React.useState("games");
  const [tabState, setTabState] = React.useState(true);
  const [rank1, setRank1] = React.useState("general");
  const [rank2, setRank2] = React.useState("general");
  const [games, setGames] = React.useState([]);
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    setQueryMain(queryMain);
    navigate(`/search/${queryMain}`);
    if (query === undefined) {
      console.log("undefined!");
      getAllGames();
    } else {
      searchGames(queryMain);
    }
  };
  const searchGames = (query) => {
    fetch(`http://localhost:8080/api/games/search/${query}`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setGames(data);
      });
  };
  const getAllGames = () => {
    fetch(`http://localhost:8080/api/games/`)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setGames(data);
      });
  };
  // React.useEffect(() => {
  //   searchGames(queryMain);
  // }, [queryMain]);
  React.useEffect(() => {
    if (mountedRef.current) {
      mountedRef.current = false;
    } else {
      console.log("game: " + games);
    }
  }, [games]);
  console.log();
  return (
    <div className="search-container">
      <div className="results-wrapper">
        <h1>
          <span className="search-title">
            Sea<span className="search-gold">r</span>ch
          </span>
          &nbsp;&nbsp;|&nbsp;&nbsp;Merakey
        </h1>
        <form className="main-search" onSubmit={(e) => handleSubmit(e)}>
          <input
            className="main-search-box"
            onSubmit={(e) => handleSubmit(e)}
            value={queryMain}
            onChange={(event) => setQueryMain(event.target.value)}
          ></input>
          <button
            className="main-submit"
            type="button"
            onClick={(e) => handleSubmit(e)}
          >
            <img src={searchIco} />
            Search
          </button>
        </form>
        <div className="filters">
          <div className="filterTabs">
            <ul>
              <li
                className={mainTab === "games" ? "active" : ""}
                onClick={() => setMainTab("games")}
              >
                GAMES 99+<div className="border-bottom"></div>
              </li>
              <li
                className={mainTab === "questions" ? "active" : ""}
                onClick={() => setMainTab("questions")}
              >
                QUESTIONS 99+
              </li>
              <li
                className={mainTab === "genres" ? "active" : ""}
                onClick={() => setMainTab("genres")}
              >
                GENRES
              </li>
              <li
                className={mainTab === "users" ? "active" : ""}
                onClick={() => setMainTab("users")}
              >
                USERS
              </li>
            </ul>
          </div>
          <div
            className={`ranking-btns ${
              mainTab === "games" && tabState ? "show" : ""
            }`}
          >
            <div className="rank-groups">
              <ul className="rank-one">
                <li
                  className={rank1 === "general" ? "active" : ""}
                  onClick={() => setRank1("general")}
                >
                  General
                </li>
                <li
                  className={rank1 === "views" ? "active" : ""}
                  onClick={() => setRank1("views")}
                >
                  most viewed
                </li>
                <li
                  className={rank1 === "recents" ? "active" : ""}
                  onClick={() => setRank1("recents")}
                >
                  most recent
                </li>
                <li
                  className={rank1 === "plays" ? "active" : ""}
                  onClick={() => setRank1("plays")}
                >
                  most played
                </li>
                <li
                  className={rank1 === "comments" ? "active" : ""}
                  onClick={() => setRank1("comments")}
                >
                  most commented
                </li>
              </ul>
              <ul className="rank-two">
                <li
                  className={rank2 === "general" ? "active" : ""}
                  onClick={() => setRank2("general")}
                >
                  General
                </li>
                <li
                  className={rank2 === "2" ? "active" : ""}
                  onClick={() => setRank2("2")}
                >
                  2 mins
                </li>
                <li
                  className={rank2 === "5" ? "active" : ""}
                  onClick={() => setRank2("5")}
                >
                  5 mins
                </li>
                <li
                  className={rank2 === "10" ? "active" : ""}
                  onClick={() => setRank2("10")}
                >
                  10 mins
                </li>
                <li
                  className={rank2 === "30" ? "active" : ""}
                  onClick={() => setRank2("30")}
                >
                  30 mins
                </li>
                <li
                  className={rank2 === "60" ? "active" : ""}
                  onClick={() => setRank2("60")}
                >
                  60 mins +
                </li>
              </ul>
            </div>
          </div>
          <div
            className={`hide-filter ${
              mainTab === "games" && tabState ? "show" : ""
            }`}
            onClick={() => setTabState(false)}
          >
            Close filter
            <img src={arrowUpIco} />
          </div>
          <div
            className={`hide-filter ${
              mainTab === "games" && !tabState ? "show" : ""
            }`}
            onClick={() => setTabState(true)}
          >
            Open filter
            <img src={arrowDownIco} />
          </div>
        </div>
        <div className="result-cards-container">
          {games.map((game) => (
            <div className="result-card" key={game.id}>
              <div className="cover">
                <img src={game.coverR}></img>
              </div>
              <div className="card-descriptions">
                {game.name}
                <div className="meta-data">
                  <p className="views">
                    <img src={playIco} />
                    {game.players}
                  </p>
                  <p className="time">
                    <img src={clockIco} />
                    {game.date}
                  </p>
                </div>
                <div className="meta-data">
                  <p className="author">
                    <img src={userIco} />
                    {game.authorId}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
export default SearchPage;
