import React, { useState } from "react";
// import {
//   HashRouter as Router,
//   Switch,
//   Route,
//   Routes,
//   Link,
//   Redirect,
// } from "react-router-dom";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { useEffect } from "react";

import logo from "./logo.svg";
import "./App.css";
import NavBar from "./components/navBar";
import HomePage from "./components/homePage";
import GameDetails from "./components/gameDetails";
import PlayGame from "./components/playGame";
import AboutPage from "./components/aboutPage";
import SubjectsPage from "./components/subjectsPage";
import SearchPage from "./components/searchPage";
import Login from "./components/login";
import SignUp from "./components/signup";
import UserHome from "./components/user/userHome";
import PastProblems from "./components/user/pastProblems";
import SubjectTopics from "./components/user/subjectTopics";
import UserComments from "./components/user/userComments";

import IsLoggedIn from "./components/utilities/loginStatus";

function App() {
  return (
    <div className="App">
      <BrowserRouter>
        <NavBar />
        <Routes>
          <Route exact path="/" element={<HomePage />} />
          <Route exact path="/games/:id" element={<GameDetails />} />
          <Route path="/games/:id/play" element={<PlayGame />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/subjects" element={<SubjectsPage />} />
          <Route path="/search/:query" element={<SearchPage />} />
          <Route path="/search/" element={<SearchPage />} />
          {IsLoggedIn() && (
            <>
              <Route path="/login" element={<Navigate to="/user" />} />
              <Route path="/signup" element={<Navigate to="/user" />} />
            </>
          )}
          {!IsLoggedIn() && (
            <>
              <Route path="/user" element={<Navigate to="/login" />} />
              <Route
                path="/user/past-problems"
                element={<Navigate to="/login" />}
              />
            </>
          )}
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />
          {/* users */}
          <Route path="/user" element={<UserHome />} />
          <Route path="/user/past-problems" element={<PastProblems />} />
          <Route path="/user/subject-topics" element={<SubjectTopics />} />
          <Route path="/user/comments" element={<SubjectTopics />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
