import "../styles/homePage.scss";
import leaderboard from "../images/leaderboard.png";
import reload from "../images/refresh-cw.svg";
import storyList from "../images/free-and-premium.png";
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

function HomePage() {
  function useForceUpdate() {
    const [value, setValue] = useState(0); // integer state
    return () => setValue((value) => value + 1); // update state to force render
    // An function that increment ðŸ‘†ðŸ» the previous state like here
    // is better than directly setting `value + 1`
  }
  const defaultCover =
    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-files/img/20210607224729.png";
  const [recent, setRecent] = React.useState([]);
  const [recentRandom, setRecentRandom] = React.useState([]);
  const [covers, setCovers] = React.useState([
    defaultCover,
    defaultCover,
    defaultCover,
    defaultCover,
    defaultCover,
    defaultCover,
  ]);
  const getMultipleRandom = (arr, num) => {
    const shuffled = [...arr].sort(() => 0.5 - Math.random());

    return shuffled.slice(0, num);
  };
  const getRecent = () => {
    fetch(`http://localhost:8080/api/games/recent/10`)
      .then((res) => res.json())
      .then((data) => {
        const recents = [];
        for (var i = 0; i < data.length; i++) {
          recents.push([data[i].id, data[i].coverR]);
        }
        setRecent(recents);
      });
  };

  React.useEffect(() => {
    getRecent();
  }, []);
  React.useEffect(() => {
    var array = getMultipleRandom(recent, 6);
    var dictionary = {};
    array.forEach(
      (key, i) =>
        (dictionary[key[0]] = { id: array[i][0], coverR: array[i][1] })
    );
    console.log("array0: " + array[0]);
    setRecentRandom(array);
    console.log("recent is updated a second time!");
  }, [recent]);
  React.useEffect(() => {
    // scroll to top
    window.scrollTo(0, 0);
  }, []);
  React.useEffect(() => {
    setCovers(
      [
        recentRandom[0][1],
        recentRandom[1][1],
        recentRandom[2][1],
        recentRandom[3][1],
        recentRandom[4][1],
        recentRandom[5][1],
      ],
      [recentRandom, setRecentRandom]
    );
  });
  const navigate = useNavigate();
  var topId = 2;
  var cardIds = [1, 3, 4, 5, 6, 7];
  return (
    <div className="homePage">
      <div className="main-home">
        <div className="leaderboard-container">
          <h3>
            <span>Leader</span>board
          </h3>
          <div className="leaderboard">
            <img src={leaderboard} />
          </div>
        </div>
        <div className="new-feed">
          <div>
            <div>
              <div
                className="story-wide story-card"
                style={{
                  backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209022318918.png"
                  )`,
                }}
                onClick={() => navigate(`/games/${topId}`)}
              ></div>
              <div className="story-grid">
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202202271152326.png"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${recentRandom[0][0]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "${covers[0]}"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${recentRandom[1][0]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209030146790.png"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${recentRandom[2][0]}`)}
                ></div>
              </div>
              <div className="story-grid">
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209031503189.png"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${recentRandom[3][0]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                  "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209031554064.png"
                )`,
                  }}
                  onClick={() => navigate(`/games/${recentRandom[4][0]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                  "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209031634255.png"
                )`,
                  }}
                  onClick={() => navigate(`/games/${recentRandom[5][0]}`)}
                ></div>
              </div>
              <button className="story-card" onClick={() => getRecent()}>
                <img src={reload} /> Refresh
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="free-and-premium">
        <img src={storyList} />
      </div>
    </div>
  );
}
export default HomePage;
