import SecondSide from "./secondSide";
import React, { useEffect, useRef } from "react";
import IsLoggedIn, { logout, getCurrentUser } from "../utilities/loginStatus";
import "./styles/userSettings.scss";

function UserSettings() {
  const [name, setName] = React.useState("");
  const [avatar, setAvatar] = React.useState("");
  const [status, setStatus] = React.useState("");
  if (getCurrentUser) {
    console.log(getCurrentUser);
    var username = getCurrentUser.username;
    var email = getCurrentUser.email;
    var uid = getCurrentUser.id;
    var roles = [];
    getCurrentUser.roles.forEach((x, i) =>
      roles.push(x.slice(5).toLowerCase())
    );
  }
  const updateProfile = (avatar, name) => {
    const data = { id: uid, avatar: avatar, displayName: name };
    fetch(`http://localhost:8080/api/profiles`, {
      method: "PUT", // or 'PUT'
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
        alert("Profile is updated successfully.");
        document.location.reload();
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  if (getCurrentUser) {
    console.log(getCurrentUser);
    var username = getCurrentUser.username;
    var email = getCurrentUser.email;
    var id = getCurrentUser.id;
    var roles = [];
    getCurrentUser.roles.forEach((x, i) =>
      roles.push(x.slice(5).toLowerCase())
    );
    console.log(roles);
  }
  const getUser = (userId, field) => {
    fetch(`http://localhost:8080/api/profiles/${userId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setAvatar(data.avatar);
        setName(data.displayName);
        setStatus(data.status);
      });
  };

  React.useEffect(() => {
    getUser(id);
  }, []);

  const handleSubmit = () => {
    var avatar = document.getElementById("avatar").value;
    var name = document.getElementById("name").value;
    var status = document.getElementById("status").value;
    if (validate(avatar, name)) {
      console.log(avatar, name);
      updateProfile(avatar, name);
    }
  };
  const validate = (avatar, name) => {
    if (!existCheck(avatar, name)) {
      alert("Fields cannot be empty!");
      return false;
    } else if (!specialCharacterCheck(name)) {
      alert("No special characters allowed.");
      return false;
    } else if (!lengthCheck(name)) {
      alert("Name is too long!");
      return false;
    } else {
      return true;
    }
  };
  const existCheck = (name, avatar) => {
    if (name == "" || avatar == "") {
      return false;
    } else {
      return true;
    }
  };
  const specialCharacterCheck = (name) => {
    var stringWithout = name.replace(
      /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/,
      ""
    );
    if (stringWithout != name) {
      return false;
    } else {
      return true;
    }
  };
  const lengthCheck = (name) => {
    if (name.length >= 25) {
      return false;
    } else {
      return true;
    }
  };
  return (
    <>
      <SecondSide />
      <section class="user-main" style={{ color: `white` }}>
        <div className="main-container">
          <h1>Settings</h1>
          <input
            className="setting-field"
            id="name"
            placeholder="Please input display name…"
            value={name}
          />
          <input
            className="setting-field"
            id="avatar"
            placeholder="Please input your avatar link…"
            value={avatar}
          />
          <br />
          <select id="status" name="status">
            <option value="none">== Choose Status ==</option>
            <option value="gaming">{`-> 🎮 Gaming`}</option>
            <option value="offline">{`-> 🤕 Offline`}</option>
            <option value="secret">{`-> 🤫 Secret`}</option>
          </select>

          <div className="submit-btn" onClick={() => handleSubmit()}>
            UPDATE SETTINGS
          </div>
        </div>
        <div className="bg-overlay" />
      </section>
    </>
  );
}
export default UserSettings;
