  React.useEffect(() => {
    // scroll to top
    window.scrollTo(0, 0);
  }, []);
  const [playTime, setPlayTime] = React.useState([]);
  const submitJSON = () => {
    var jsonText = document.getElementById("jsonText").value;
    console.log(JSON.parse(jsonText));
  };
  return (
    <>
      <SecondSide />
      <section class="user-main">
        <div className="addStory">
          <h2>
            <img />
            Add a story
          </h2>
          <textarea id="jsonText" placeholder="Please input jsonâ€¦"></textarea>
          <div className="submit-btn" onClick={() => submitJSON()}>
            Submit JSON
          </div>
        </div>
        <p>Estimated play time: {playTime}</p>
      </section>
    </>
  );
}
export default AddStory;
