import SecondSide from "./secondSide";
import React, { useEffect, useRef } from "react";
import IsLoggedIn, { logout, getCurrentUser } from "../utilities/loginStatus";
import "./styles/userComments.scss";

function UserComments() {
  const [comments, setComments] = React.useState([]);
  const getComments = (userId) => {
    fetch(`http://localhost:8080/api/profiles/${userId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setComments(data.comments);
      });
  };
  if (getCurrentUser) {
    console.log(getCurrentUser);
    var username = getCurrentUser.username;
    var email = getCurrentUser.email;
    var id = getCurrentUser.id;
    var roles = [];
    getCurrentUser.roles.forEach((x, i) =>
      roles.push(x.slice(5).toLowerCase())
    );
    console.log(roles);
  }
  React.useEffect(() => {
    getComments(id);
  }, []);
  React.useEffect(() => {
    // scroll to top
    window.scrollTo(0, 0);
  }, []);
  return (
    <>
      <SecondSide />
      <section class="user-main">
        {comments.map((comment) => (
          <>
            <div className="comment-list">
              {comment.id}: {comment.text};<br />
              time: {comment.createdAt}, game:
              {comment.gameId},&nbsp;
              {comment.parentId !== null && `Replying to: ${comment.parentId}`}
            </div>
          </>
        ))}

        <table style={{ width: `100%`, color: `white` }}>
          <tr>
            <td>id</td>
            <td>Parent id</td>
            <td>Content</td>
            <td>Game</td>
            <td>Date</td>
            <td>Actions</td>
          </tr>
          {comments.map((comment) => (
            <tr>
              <td>{comment.id}</td>
              <td>{comment.parentId !== null && comment.parentId}</td>{" "}
              <td>{comment.text}</td>
              <td>{comment.gameId}</td>
              <td>{comment.createdAt}</td>
              {comment.gameId},&nbsp;
            </tr>
          ))}
        </table>
        <div className="bg-overlay" />
      </section>
    </>
  );
}
export default UserComments;
