import SecondSide from "./secondSide";
import React, { useEffect, useRef } from "react";
import IsLoggedIn, { logout, getCurrentUser } from "../utilities/loginStatus";
import "./styles/userSettings.scss";

function UserSettings() {
  const handleSubmit = () => {
    var avatar = document.getElementById("avatar");
    var name = document.getElementById("name");
    if (validate(avatar, name)) {
      console.log(avatar, name);
    }
  };
  const validate = (avatar, name) => {
    if (!specialCharacterCheck(name)) {
      alert("No special characters allowed.");
    }
    return false;
  };
  const existCheck = (name, avatar) => {
    if (name == "" || avatar == "") {
      return false;
    }
  };
  const specialCharacterCheck = (name) => {
    var stringWithout = name.replace(/[^a-zA-Z]/, "");
    if (stringWithout != name) {
      return false;
    } else {
      return true;
    }
  };
  return (
    <>
      <SecondSide />
      <section class="user-main" style={{ color: `white` }}>
        <div className="main-container">
          <h1>Settings</h1>
          <input
            className="setting-field"
            id="avatar"
            placeholder="Please input your avatar link…"
          />
          <input
            className="setting-field"
            id="name"
            placeholder="Please input your displayed name…"
          />
          <div className="submit-btn">UPDATE SETTINGS</div>
        </div>
        <div className="bg-overlay" onClick={() => handleSubmit()} />
      </section>
    </>
  );
}
export default UserSettings;
