import SecondSide from "./secondSide";
import React, { useEffect, useRef } from "react";
import IsLoggedIn, { logout, getCurrentUser } from "../utilities/loginStatus";

function YourGames() {
  React.useEffect(() => {
    // scroll to top
    window.scrollTo(0, 0);
    getGames(id);
  }, []);
  if (getCurrentUser) {
    console.log(getCurrentUser);
    var username = getCurrentUser.username;
    var email = getCurrentUser.email;
    var id = getCurrentUser.id;
    var roles = [];
    getCurrentUser.roles.forEach((x, i) =>
      roles.push(x.slice(5).toLowerCase())
    );
    console.log(roles);
  }
  const [games, setGames] = React.useState([]);
  const getGames = (userId) => {
    fetch(`http://localhost:8080/api/profiles/games/${userId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setGames(data.games);
      });
  };
  return (
    <>
      <SecondSide />
      <section className="user-main">
        <h2>Your Games</h2>
        {games.map((game) => (
          <>
            {game.name}, {game.description}
            <br />
            <br />
          </>
        ))}
        {games.map((game) => (
          <div
            className="result-card"
            key={game.id}
            onClick={() => navigate(`/games/${game.id}`)}
          >
            <div className="cover">
              <img src={game.coverR}></img>
            </div>
            <div className="card-descriptions">
              <span>{game.name}</span>
              <div className="meta-data">
                <p className="views">
                  <img src={playIco} />
                  {game.views}
                </p>
                <p className="time">
                  <img src={clockIco} />
                  {game.createdAt.split("T")[0]}
                </p>
              </div>
            </div>
          </div>
        ))}
      </section>
    </>
  );
}
export default YourGames;
