import "../styles/homePage.scss";
import leaderboard from "../images/leaderboard.png";
import reload from "../images/refresh-cw.svg";
import storyList from "../images/free-and-premium.png";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

function HomePage() {
  React.useEffect(() => {
    fetch(`http://localhost:8080/api/games/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setName(data.name);
        setDesc(data.description);
        setAuthorId(data.authorId);
        setPlayers(data.players);
        setCover(data.coverW);
      });
  }, []);
  React.useEffect(() => {
    // scroll to top
    window.scrollTo(0, 0);
  }, []);
  const navigate = useNavigate();
  var topId = 2;
  var cardIds = [1, 3, 4, 5, 6, 7];
  return (
    <div className="homePage">
      <div className="main-home">
        <div className="leaderboard-container">
          <h3>
            <span>Leader</span>board
          </h3>
          <div className="leaderboard">
            <img src={leaderboard} />
          </div>
        </div>
        <div className="new-feed">
          <div>
            <div>
              <div
                className="story-wide story-card"
                style={{
                  backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209022318918.png"
                  )`,
                }}
                onClick={() => navigate(`/games/${topId}`)}
              ></div>
              <div className="story-grid">
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202202271152326.png"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${cardIds[0]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209030126559.png"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${cardIds[1]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209030146790.png"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${cardIds[2]}`)}
                ></div>
              </div>
              <div className="story-grid">
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                    "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209031503189.png"
                  )`,
                  }}
                  onClick={() => navigate(`/games/${cardIds[3]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                  "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209031554064.png"
                )`,
                  }}
                  onClick={() => navigate(`/games/${cardIds[4]}`)}
                ></div>
                <div
                  className="card story-card"
                  style={{
                    backgroundImage: `url(
                  "https://cdn.jsdelivr.net/gh/liangchuxin/blog-imgs/img/202209031634255.png"
                )`,
                  }}
                  onClick={() => navigate(`/games/${cardIds[5]}`)}
                ></div>
              </div>
              <button className="story-card">
                <img src={reload} /> Refresh
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="free-and-premium">
        <img src={storyList} />
      </div>
    </div>
  );
}
export default HomePage;
