import "../styles/search.scss";
import React, { useEffect, useState } from "react";

export function Search() {
  const closeSearch = () => {
    window.localStorage.setItem("search", "closed");
  };
  const openSearch = () => {
    return (
      <>
        <div className="search-box-backdrop"></div>
        <div className="search">
          <form
            class="search-form"
            name="search"
            method="post"
            action="https://cattila.becomingcelia.com/zb_system/cmd.php?act=search"
          >
            <input
              class="search-field"
              id="searchfield"
              type="text"
              name="q"
              placeholder="Search games and subject topics‚Ä¶"
            />
            <button
              class="appear-button always-appear"
              type="submit"
              id="searchsubmit"
            >
              üîç<i class="icon icon-search" aria-hidden="true"></i>
            </button>
            <button class="search-button">
              <i class="icon icon-search" aria-hidden="true"></i>
            </button>
          </form>
        </div>
        <button
          onClick={() => closeSearch()}
          class="search-wrapper-close"
          aria-label="Close"
        ></button>
      </>
    );
  };
}
