export default IsLoggedIn = () => {
  if (localStorage.getItem("user")) {
    return true;
  } else {
    return false;
  }
};

export const logOut = () => {
  localStorage.removeItem("user");
};

export const getCurrentUser = JSON.parse(localStorage.getItem("user"));
