import "../styles/searchPage.scss";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import searchIco from "../images/search.svg";
function SearchPage() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  const [mainTab, setMainTab] = React.useState("games");
  const [rank1, setRank1] = React.useState("general");
  const [rank2, setRank2] = React.useState("general");
  const navigate = useNavigate();
  return (
    <div className="search-container">
      <div className="results-wrapper">
        <h1>
          <span className="search-title">
            Sea<span className="search-gold">r</span>ch
          </span>
          &nbsp;&nbsp;|&nbsp;&nbsp;Merakey
        </h1>
        <form className="main-search">
          <input className="main-search-box"></input>
          <button className="main-submit">
            <img src={searchIco} />
            Search
          </button>
        </form>
        <div className="filters">
          <div className="filterTabs">
            <ul>
              <li
                className={mainTab === "games" ? "active" : ""}
                onClick={() => setMainTab("games")}
              >
                GAMES 99+<div className="border-bottom"></div>
              </li>
              <li
                className={mainTab === "questions" ? "active" : ""}
                onClick={() => setMainTab("questions")}
              >
                QUESTIONS 99+
              </li>
              <li
                className={mainTab === "genres" ? "active" : ""}
                onClick={() => setMainTab("genres")}
              >
                GENRES
              </li>
              <li
                className={mainTab === "users" ? "active" : ""}
                onClick={() => setMainTab("users")}
              >
                USERS
              </li>
            </ul>
          </div>
          <div className="ranking-btns">
            <div className="rank-groups">
              <ul className="rank-one">
                <li
                  className={rank1 === "general" ? "active" : ""}
                  onClick={() => setRank1("general")}
                >
                  General
                </li>
                <li
                  className={rank1 === "views" ? "active" : ""}
                  onClick={() => setRank1("views")}
                >
                  most viewed
                </li>
                <li
                  className={rank1 === "recents" ? "active" : ""}
                  onClick={() => setRank1("recents")}
                >
                  most recent
                </li>
                <li
                  className={rank1 === "plays" ? "active" : ""}
                  onClick={() => setRank1("plays")}
                >
                  most played
                </li>
                <li
                  className={rank1 === "comments" ? "active" : ""}
                  onClick={() => setRank1("comments")}
                >
                  most commented
                </li>
              </ul>
              <ul className="rank-two">
                <li
                  className={rank2 === "general" ? "active" : ""}
                  onClick={() => setRank2("general")}
                >
                  General
                </li>
                <li
                  className={rank2 === "2" ? "active" : ""}
                  onClick={() => setRank2("2")}
                >
                  2 mins
                </li>
                <li
                  className={rank2 === "5" ? "active" : ""}
                  onClick={() => setRank2("5")}
                >
                  5 mins
                </li>
                <li>10 mins</li>
                <li>30 mins</li>
                <li>60 mins +</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default SearchPage;
