import "../styles/search.scss";
import React, { useEffect, useState } from "react";

export function Search(props) {
  React.useEffect(() => {
    const show = props.show;

    //Ê†πÊçÆ‰º†ËøõÊù•ÁöÑÊîπÂèò state
    if (show) {
      setSearch(true);
    } else {
      setSearch(false);
    }
  });

  const [search, setSearch] = React.useState(false);
  // Ê†πÊçÆËá™Â∑±ÁöÑÂáΩÊï∞ÊîπÂèò
  const closeSearch = () => {
    console.log("Search box is closed");
    setSearch(false);
  };
  //   const openSearch = () => {
  //     console.log("Search box is opened");
  //   };

  React.useEffect(() => {
    if (search) {
      return (
        <>
          <div
            className="search-box-backdrop"
            onClick={() => closeSearch()}
          ></div>
          <div className="search">
            <form
              class="search-form"
              name="search"
              method="post"
              action="https://cattila.becomingcelia.com/zb_system/cmd.php?act=search"
            >
              <input
                class="search-field"
                id="searchfield"
                type="text"
                name="q"
                placeholder="Search games and subject topics‚Ä¶"
              />
              <button
                class="appear-button always-appear"
                type="submit"
                id="searchsubmit"
              >
                üîç<i class="icon icon-search" aria-hidden="true"></i>
              </button>
              <button class="search-button">
                <i class="icon icon-search" aria-hidden="true"></i>
              </button>
            </form>
          </div>
          <button
            onClick={() => closeSearch()}
            class="search-wrapper-close"
            aria-label="Close"
          ></button>
        </>
      );
    } else {
      return null;
    }
  });
}
