import SecondSide from "./secondSide";
import React, { useEffect, useRef } from "react";
import IsLoggedIn, { logout, getCurrentUser } from "../utilities/loginStatus";
import { useNavigate, useParams } from "react-router-dom";
import clockIco from "../../images/clock.svg";
import playIco from "../../images/play-circle.svg";
import "./styles/yourGames.scss";

function YourGames() {
  const navigate = useNavigate();
  React.useEffect(() => {
    // scroll to top
    window.scrollTo(0, 0);
    getGames(id);
  }, []);
  if (getCurrentUser) {
    console.log(getCurrentUser);
    var username = getCurrentUser.username;
    var email = getCurrentUser.email;
    var id = getCurrentUser.id;
    var roles = [];
    getCurrentUser.roles.forEach((x, i) =>
      roles.push(x.slice(5).toLowerCase())
    );
    console.log(roles);
  }
  const [games, setGames] = React.useState([]);
  const [tabState, setTabState] = React.useState("all");
  const getGames = (userId) => {
    fetch(`http://localhost:8080/api/profiles/games/${userId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setGames(data.games);
      });
  };
  function renderGameCard(game) {
    return (
      <div
        className="game-card"
        key={game.id}
        onClick={() => navigate(`/games/${game.id}`)}
      >
        <div className="cover">
          <img src={game.coverR} />
        </div>
        <div className="card-descriptions">
          <span>{game.name}</span>
          <div className="meta-data">
            <p className="views">
              <img src={playIco} />
              {game.views}
            </p>
            <p className="time">
              <img src={clockIco} />
              {game.createdAt.split("T")[0]}
            </p>
          </div>
        </div>
      </div>
    );
  }
  return (
    <>
      <SecondSide />
      <section className="user-main">
        <div className="title-container">
          <h2>Your Games ({games.length})</h2>
          <div className="menu">
            <ul>
              <li
                className={`tab ${tabState == "all" && "active"}`}
                onClick={() => setTabState("all")}
              >
                All
              </li>
              <li
                className={`tab ${tabState == "published" && "active"}`}
                onClick={() => setTabState("published")}
              >
                Published
              </li>
              <li
                className={`tab ${tabState == "drafts" && "active"}`}
                onClick={() => setTabState("drafts")}
              >
                Drafts
              </li>
            </ul>
          </div>
        </div>
        <div className="games-container">
          {games.map((game) =>
            tabState == "published" ? (
              game.published == true && (
                <div
                  className="game-card"
                  key={game.id}
                  onClick={() => navigate(`/games/${game.id}`)}
                >
                  <div className="cover">
                    <img src={game.coverR}></img>
                  </div>
                  <div className="card-descriptions">
                    <span>{game.name}</span>
                    <div className="meta-data">
                      <p className="views">
                        <img src={playIco} />
                        {game.views}
                      </p>
                      <p className="time">
                        <img src={clockIco} />
                        {game.createdAt.split("T")[0]}
                      </p>
                    </div>
                  </div>
                </div>
              )
            ) : tabState == "drafts" ? (
              game.published == false && (
                <div
                  className="game-card"
                  key={game.id}
                  onClick={() => navigate(`/games/${game.id}`)}
                >
                  <div className="cover">
                    <img src={game.coverR}></img>
                  </div>
                  <div className="card-descriptions">
                    <span>{game.name}</span>
                    <div className="meta-data">
                      <p className="views">
                        <img src={playIco} />
                        {game.views}
                      </p>
                      <p className="time">
                        <img src={clockIco} />
                        {game.createdAt.split("T")[0]}
                      </p>
                    </div>
                  </div>
                </div>
              )
            ) : (
              <div
                className="game-card"
                key={game.id}
                onClick={() => navigate(`/games/${game.id}`)}
              >
                <div className="cover">
                  <img src={game.coverR}></img>
                </div>
                <div className="card-descriptions">
                  <span>{game.name}</span>
                  <div className="meta-data">
                    <p className="views">
                      <img src={playIco} />
                      {game.views}
                    </p>
                    <p className="time">
                      <img src={clockIco} />
                      {game.createdAt.split("T")[0]}
                    </p>
                  </div>
                </div>
              </div>
            )
          )}
        </div>
      </section>
    </>
  );
}
export default YourGames;
