import SecondSide from "./secondSide";
import React, { useEffect, useRef } from "react";
import IsLoggedIn, { logout, getCurrentUser } from "../utilities/loginStatus";

function YourGames() {
  React.useEffect(() => {
    // scroll to top
    window.scrollTo(0, 0);
    getGames();
  }, []);
  const [games, setGames] = React.useState([]);
  const getGames = (userId) => {
    fetch(`http://localhost:8080/api/profiles/games/${userId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setGames(data.games);
      });
  };
  return (
    <>
      <SecondSide />
      <section className="user-main"></section>
    </>
  );
}
export default YourGames;
