              </button>
              <button class="search-button">
                <i class="icon icon-search" aria-hidden="true"></i>
              </button>
            </form>
          </div>
          <button
            onClick={() => closeSearch()}
            class="search-wrapper-close"
            aria-label="Close"
          ></button>
        </div>
      </>
    );
  } else if (searchState === false) {
    return null;
